import { ErrorMessage, Field, Form, useFormik } from "formik";
import React from "react";

function App() {
  const form = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    onSubmit: () => {
      alert("Login Successful");
    },
    validate: (values) => {
      const errors = {};
      if (!values.email) {
        errors.email = "field required";
      }
      if (!values.password) {
        errors.password = "field required";
      }
      if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)) {
        errors.email = "Username should be an email";
      }
      return errors;
    },
  });

  return (
    <div>
      <Form onSubmit={form.handleSubmit}>
        <div>Email:</div>
        <Field id="emailField" type="email" name="email" />
        {form.errors.email && <div id="emailError">{form.errors.email}</div>}
        <ErrorMessage name="email" component="div" />
        <div>Password:</div>
        <Field id="pswField" type="password" name="password" />
        <ErrorMessage id="pswError" name="password" component="div" />
        <button id="submitBtn" type="submit" disabled={isSubmitting}>
          Submit
        </button>
      </Form>
    </div>
  );
}

export default App;
