import { useFormik } from "formik";
import React from "react";

function App() {
  const form = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    onSubmit: () => {},
    validate: (values) => {
      let err = {};
      if (!values.email) {
        err.email = "Field required";
      }
      if (!values.password) {
        err.password = "Field required";
      }
    },
  });

  return (
    <div>
      <form onSubmit={form.handleSubmit}>
        <div>Email:</div>
        <input
          type="text"
          onChange={form.handleChange}
          value={form.values.email}
        />
        {form.err.email ? <div>{form.err.email}</div> : null}
        <div>Password:</div>
        <input
          type="text"
          onChange={form.handleChange}
          value={form.values.password}
        />
        <br />
        {form.err.password ? (
          <div id="pswError">{form.err.password}</div>
        ) : null}
        <button id="submitBtn" type="submit">
          Submit
        </button>
      </form>
    </div>
  );
}

export default App;
