import { useFormik } from "formik";
import { signupValidationSchema } from "../../validationSchemas";

export const useSignupFormik = () => {
  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    onSubmit: () => {
      alert("Login Successful");
    },
    validationSchema: signupValidationSchema,
  });

  return formik;
};
